import React, { useEffect, useState } from 'react';
import Header from '../components/Header';
import { useLocation } from 'react-router-dom';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import SearchResults from './pages/SearchResult';

const API_BASE_URL = 'http://localhost:5231';

const HomePage = () => {
    const [books, setBooks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const location = useLocation();

    useEffect(() => {
        const fetchBooks = async () => {
            const params = new URLSearchParams(location.search);
            const query = params.get('query');
            const genreId = params.get('genreId');
            let url = `${API_BASE_URL}/api/book`;

            if (query) {
                url += `/search/${query}`; // Search by title
            } else if (genreId) {
                url += `/genre/${genreId}`; // Filter by genre
            }

            try {
                const response = await fetch(url);
                if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
                const data = await response.json();
                setBooks(data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching books:', error);
                setError(error.message);
                setLoading(false);
            }
        };

        fetchBooks();
    }, [location.search]);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div>
            <Header />
            <div className="container mt-4">
                <h2 className="mb-4">Kết quả tìm kiếm</h2>
                <div className="row">
                    {books.map(book => (
                        <div key={book.bookId} className="col-md-3 mb-4">
                            <div className="card">
                                <div className="card-body">
                                    <h5 className="card-title">{book.title}</h5>
                                    <p className="card-text">{book.author}</p>
                                    <p className="card-text">Giá: {book.price} VNĐ</p>
                                    <Link to={`/book/${book.bookId}`} className="btn btn-primary">Chi tiết</Link>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default HomePage;
