import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Header from '../../components/Header';
import '../../components/BookDetail.css';
import { getUserIdFromToken } from '../../services/userAuth';

const API_BASE_URL = 'http://localhost:5231';

const BookDetails = () => {
  const { id } = useParams();
  const [book, setBook] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBook = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}/api/book/${id}`);
        
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const bookData = await response.json();
        setBook(bookData);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching book:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchBook();
  }, [id]);

  const handleAddToCart = async () => {
    const userId = getUserIdFromToken();
    if (!userId) {
      alert('Please log in before adding to the cart.');
      return;
    }

    const payload = {
      userId: userId,
      bookId: book.bookId,
      quantity: parseInt(quantity),
    };

    try {
      const response = await fetch(`${API_BASE_URL}/api/cart`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(payload),
      });

      if (response.ok) {
        alert('Added to cart successfully!');
      } else {
        const err = await response.json();
        alert(`Error adding to cart: ${err.message || response.statusText}`);
      }
    } catch (err) {
      console.error('API call error:', err);
      alert('An error occurred, please try again later.');
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!book) return <div>No book found.</div>;

  return (
    <div>
      <Header />
      <div className="container mt-4">
        <div className="book-info">
          <div className="row">
            <div className="col-md-12">
              <h2>{book.title}</h2>
              <p><strong>Author:</strong> {book.author}</p>
              <p><strong>Publisher:</strong> {book.publisherName}</p>
              <p><strong>Genre:</strong> {book.genreName}</p>
              <p><strong>ISBN:</strong> {book.isbn}</p>
              <p><strong>Published Year:</strong> {book.publishedYear}</p>
              <p><strong>Quantity:</strong> {book.quantity}</p>
              <p><strong>Price:</strong> {book.price} VND</p>
              <div className="mb-3">
                <label htmlFor="quantity" className="form-label">Select Quantity:</label>
                <input
                  type="number"
                  id="quantity"
                  className="form-control"
                  value={quantity}
                  onChange={(e) => setQuantity(e.target.value)}
                  min="1"
                  max={book.quantity}
                />
              </div>
              <button className="btn btn-primary" onClick={handleAddToCart}>
                Add to Cart
              </button>
            </div>
          </div>
        </div>

        <div className="reviews-section mt-5">
          <h3>Reviews</h3>
          {book.Reviews && book.Reviews.length > 0 ? (
            book.Reviews.map((review) => (
              <div key={review.reviewId} className="review-card mb-3">
                <p><strong>Rating:</strong> {review.rating}</p>
                <p>{review.comment}</p>
              </div>
            ))
          ) : (
            <p>No reviews yet.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default BookDetails;
