import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Header.css';

const API_BASE_URL = 'http://localhost:5231';

const Header = () => {
    const navigate = useNavigate();
    const isAuthenticated = localStorage.getItem('token') !== null;
    const username = localStorage.getItem('username');
    const [genres, setGenres] = useState([]);

    useEffect(() => {
        const fetchGenres = async () => {
            try {
                const response = await fetch(`${API_BASE_URL}/api/genre`);
                if (!response.ok) {
                    throw new Error('Failed to fetch genres');
                }
                const data = await response.json();
                setGenres(data);
            } catch (error) {
                console.error('Error fetching genres:', error);
            }
        };

        fetchGenres();
    }, []);

    const handleLoginClick = () => {
        if (!isAuthenticated) {
            navigate('/login');
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('userId');
        localStorage.removeItem('username');
        
        navigate('/login');
        window.location.reload();
    };

    const handleGenreSelect = (genreId) => {
        console.log(`Selected Genre ID: ${genreId}`);
    };

    return (
        <header className="header bg-light shadow-sm p-3">
            <div className="container d-flex justify-content-between align-items-center">
                <div className="logo">
                    <h1 className="text-danger logo-title">BOOK RENTAL WEBSITE</h1>
                </div>
                <nav className="nav">
                    <ul className="list-unstyled d-flex align-items-center">
                        <li className="me-3">
                            <button 
                                className="btn btn-outline-dark"
                                onClick={() => navigate('/cart')}
                            >
                                Cart
                            </button>
                        </li>
                        {isAuthenticated ? (
                            <>
                                <li className="me-3">
                                    <span className="text-dark">Xin chào, {username}</span>
                                </li>
                                <li>
                                    <button 
                                        className="btn btn-danger"
                                        onClick={handleLogout}
                                    >
                                        Logout
                                    </button>
                                </li>
                            </>
                        ) : (
                            <li>
                                <button 
                                    className="btn btn-danger"
                                    onClick={handleLoginClick}
                                >
                                    Login
                                </button>
                            </li>
                        )}
                    </ul>
                </nav>
            </div>
            <div className="genre-search-container d-flex justify-content-center mt-3">
                <div className="genre-search-wrapper d-flex justify-content-between" style={{ width: '75%' }}>
                    <select className="form-select" onChange={(e) => handleGenreSelect(e.target.value)}>
                        <option value="">All Genre</option>
                        {genres.map(genre => (
                            <option key={genre.genreId} value={genre.genreId}>
                                {genre.genreName}
                            </option>
                        ))}
                    </select>
                    <div className="search-container">
                        <input type="text" className="form-control" placeholder="Search for book, author..." />
                        <button className="btn btn-danger">Search</button>
                    </div>
                </div>
            </div>
        </header>
    );
};

export default Header;
