import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { getAuthHeaders } from '../../services/authService';
import '../../components/CSS/dashboard.css';

const DashboardPage = () => {
    const [user, setUser] = useState({ role: null, username: 'Guest' });
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUser = async () => {
            try {
                const userData = await getAuthHeaders(); 
            } catch (error) {
                console.error('Failed to fetch user:', error);
            }
        };

        fetchUser();
    }, []);

    useEffect(() => {
        if (user.role !== 1) {
            navigate('/');
        }
    }, [user.role, navigate]);

    return (
        <div className="dashboard-container">
            <h2>Admin Dashboard</h2>
            <p>Welcome, {user.username}!</p>
            
            <div className="dashboard-buttons">
                <Link to="/manage-books" className="dashboard-button">Book Management</Link>
                <Link to="/manage-genres" className="dashboard-button">Manage Genres</Link>
                <Link to="/manage-publishers" className="dashboard-button">Manage Publishers</Link>
                <Link to="/manage-admin-accounts" className="dashboard-button">Manage Admin Accounts</Link>
                <Link to="/manage-staff-accounts" className="dashboard-button">Manage Staff Accounts</Link>
                <Link to="/manage-lesse-accounts" className="dashboard-button">Manage Lesse Accounts</Link>
                <Link to="/manage-invoices" className="dashboard-button">Manage Invoices</Link>
                <Link to="/manage-rentals" className="dashboard-button">Manage Rentals</Link>
                <Link to="/manage-rental-details" className="dashboard-button">Manage Rental Details</Link>
            </div>
        </div>
    );
};

export default DashboardPage;
