import React, { useEffect, useState } from 'react';
import Header from '../components/Header';
import { Link } from 'react-router-dom';

const HomePage = () => {
  const [newBooks, setNewBooks] = useState([]);
  const [popularBooks, setPopularBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        // Fetch newest books
        const newBooksResponse = await fetch('/api/Book?sort=newest&limit=5');
        
        // Kiểm tra nếu response không OK
        if (!newBooksResponse.ok) {
          const text = await newBooksResponse.text();
          throw new Error(`API error: ${newBooksResponse.status} - ${text}`);
        }
        
        // Kiểm tra content-type
        const contentType = newBooksResponse.headers.get('content-type');
        if (!contentType || !contentType.includes('application/json')) {
          const text = await newBooksResponse.text();
          throw new Error(`Invalid content-type: ${contentType}, Response: ${text}`);
        }
        
        const newBooksData = await newBooksResponse.json();
        setNewBooks(newBooksData);
      
        // Fetch popular Books
        const popularBooksResponse = await fetch('/api/Book?sort=popular&limit=5');
        
        if (!popularBooksResponse.ok) {
          const text = await popularBooksResponse.text();
          throw new Error(`API error: ${popularBooksResponse.status} - ${text}`);
        }
        
        const popularContentType = popularBooksResponse.headers.get('content-type');
        if (!popularContentType || !popularContentType.includes('application/json')) {
          const text = await popularBooksResponse.text();
          throw new Error(`Invalid content-type: ${popularContentType}, Response: ${text}`);
        }
        
        const popularBooksData = await popularBooksResponse.json();
        setPopularBooks(popularBooksData);

        setLoading(false);
        setError(null);
      } catch (error) {
        console.error('Error fetching books:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  if (loading) {
    return <div className="text-center py-5">Đang tải...</div>;
  }

  if (error) {
    return (
      <div className="bg-gray-50 min-h-screen">
        <Header />
        <div className="container mx-auto px-4 py-8">
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
            <strong className="font-bold">Lỗi khi tải dữ liệu!</strong>
            <span className="block sm:inline"> {error}</span>
            <p className="mt-2">Vui lòng thử lại sau hoặc liên hệ quản trị viên.</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-gray-50 min-h-screen">
      <Header />
      
      <div className="container mx-auto px-4 py-8">
        <div className="flex flex-col lg:flex-row gap-8">
          <div className="lg:w-2/3">
            <div className="bg-white rounded-lg shadow-md p-6">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-2xl font-bold text-gray-800">Sách mới</h2>
                <Link to="/Book" className="text-blue-600 hover:underline">Xem toàn bộ →</Link>
              </div>
              
              <div className="space-y-4">
                {newBooks.map(book => (
                  <div key={book.bookId} className="border-b pb-4 last:border-b-0">
                    <div className="flex justify-between">
                      <div>
                        <h3 className="font-semibold text-lg">{book.title}</h3>
                        <p className="text-gray-600">{book.author}</p>
                        {book.publisher && (
                          <p className="text-sm text-gray-500">NXB: {book.publisher.name}</p>
                        )}
                      </div>
                      <div className="text-right">
                        <p className="text-red-500 font-medium">
                          {new Intl.NumberFormat('vi-VN').format(book.price)}đ
                        </p>
                      </div>
                    </div>
                    <div className="mt-2 text-sm text-gray-500 line-clamp-2">
                      {book.description || 'Chưa có mô tả'}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
          <div className="lg:w-1/3">
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-bold text-gray-800 mb-4">Sách bán chạy</h2>
              
              <div className="space-y-4">
                {popularBooks.map((book, index) => (
                  <div key={book.bookId} className="flex items-start gap-3">
                    <span className="bg-gray-200 text-gray-700 rounded-full w-6 h-6 flex items-center justify-center text-sm">
                      {index + 1}
                    </span>
                    <div className="flex-1">
                      <h3 className="font-medium">{book.title}</h3>
                      <p className="text-sm text-gray-600">{book.author}</p>
                      <p className="text-red-500 text-sm mt-1">
                        {new Intl.NumberFormat('vi-VN').format(book.price)}đ
                      </p>
                    </div>
                    <span className="text-xs bg-blue-100 text-blue-800 px-2 py-1 rounded">
                      Đã bán: {book.rentalDetails?.length || 0}
                    </span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
