import React, { useEffect, useState } from 'react';
import Header from '../components/Header';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css'; // Nhập Bootstrap

const API_BASE_URL = 'http://localhost:5231';

const HomePage = () => {
  const [newBooks, setNewBooks] = useState([]);
  const [popularBooks, setPopularBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        // Fetch newest books
        const newBooksResponse = await fetch(`${API_BASE_URL}/api/book?sort=newest&limit=5`, {
          headers: {
            'Accept': 'application/json',
          },
          credentials: 'include'
        });

        if (!newBooksResponse.ok) {
          throw new Error(`HTTP error! status: ${newBooksResponse.status}`);
        }

        const newBooksData = await newBooksResponse.json();
        setNewBooks(newBooksData);
      
        // Fetch popular books
        const popularBooksResponse = await fetch(`${API_BASE_URL}/api/book?sort=popular&limit=5`, {
          headers: {
            'Accept': 'application/json',
          },
          credentials: 'include'
        });

        if (!popularBooksResponse.ok) {
          throw new Error(`HTTP error! status: ${popularBooksResponse.status}`);
        }

        const popularBooksData = await popularBooksResponse.json();
        setPopularBooks(popularBooksData);

        setLoading(false);
      } catch (error) {
        console.error('Error fetching books:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <Header />
      <div className="container mt-4">
        <h2 className="mb-4">Sách Mới Nhất</h2>
        <div className="row">
          {newBooks.map(book => (
            <div key={book.bookId} className="col-md-3 mb-4">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">{book.title}</h5>
                  <p className="card-text">{book.author}</p>
                  <p className="card-text">Giá: {book.price} VNĐ</p>
                  <Link to={`/books/${book.bookId}`} className="btn btn-primary">Chi tiết</Link>
                </div>
              </div>
            </div>
          ))}
        </div>

        <h2 className="mb-4 mt-5">Sách Phổ Biến</h2>
        <div className="row">
          {popularBooks.map(book => (
            <div key={book.bookId} className="col-md-3 mb-4">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">{book.title}</h5>
                  <p className="card-text">{book.author}</p>
                  <p className="card-text">Giá: {book.price} VNĐ</p>
                  <Link to={`/books/${book.bookId}`} className="btn btn-primary">Chi tiết</Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
