import React, { useEffect, useState } from 'react';
import { getAuthHeaders, getUserIdFromToken } from '../../services/userAuth';

const API_BASE_URL = 'http://localhost:5231';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCartItems = async () => {
      const userId = getUserIdFromToken();
      if (!userId) {
        setError("Không xác định được người dùng.");
        setLoading(false);
        return;
      }

      try {
        const response = await fetch(`${API_BASE_URL}/api/cart/user/${userId}`, {
          headers: getAuthHeaders(),
        });

        if (!response.ok) {
          throw new Error(`Lỗi lấy giỏ hàng: ${response.status}`);
        }

        const data = await response.json();
        setCartItems(data);
        calculateTotal(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchCartItems();
  }, []);

  const calculateTotal = (items) => {
    const totalAmount = items.reduce((acc, item) => acc + item.book.price * item.quantity, 0);
    setTotal(totalAmount);
  };

  const handleCheckout = () => {
    alert('Mua hàng thành công (giả lập)');
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <>
    <div style={{ display: 'flex', padding: '1rem', maxWidth: '100%' }}>
      <div style={{ flex: 2 }}>
        <h2>GIỎ SÁCH CỦA BẠN</h2>
        {cartItems.length === 0 ? (
          <p>Giỏ hàng của bạn đang trống.</p>
        ) : (
          cartItems.map(item => (
            <div key={item.cartId} style={{ display: 'flex', justifyContent: 'space-between', padding: '1rem', borderBottom: '1px solid #ccc' }}>
              <div>
                <strong>{item.book.title}</strong>
                <div>Tác giả: {item.book.author}</div>
              </div>
              <div>
                <div>Số lượng: {item.quantity}</div>
                <div>Đơn giá: <span style={{ color: 'red' }}>{item.book.price.toLocaleString()}đ</span></div>
              </div>
            </div>
          ))
        )}
      </div>

      <div style={{ flex: 1, paddingLeft: '2rem', borderLeft: '1px solid #ccc' }}>
        <h3>Tổng tiền: <span style={{ color: 'blue' }}>{total.toLocaleString()}đ</span></h3>
        <button style={{ padding: '0.5rem 1rem', backgroundColor: 'orange', color: 'white', border: 'none', marginTop: '1rem' }}
          onClick={handleCheckout}>
          🛒 Mua hàng
        </button>
      </div>
    </div>
    </>
    
  );
};

export default Cart;
