import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Header.css';

const Header = () => {
    const navigate = useNavigate();
    const isAuthenticated = localStorage.getItem('token') !== null;
    const username = localStorage.getItem('username');

    const handleCartClick = () => {
        if (!isAuthenticated) {
            navigate('/login');
        } else {
            navigate('/cart');
        }
    };

    const handleLoginClick = () => {
        if (!isAuthenticated) {
            navigate('/login');
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('userId');
        localStorage.removeItem('username');
        
        navigate('/login');
        
        window.location.reload();
    };

    return (
        <header className="header bg-light shadow-sm p-3">
            <div className="container d-flex justify-content-between align-items-center">
                <div className="logo">
                    <h1 className="text-danger logo-title">BOOK RENTAL WEBSITE</h1>
                </div>
                <nav className="nav">
                    <ul className="list-unstyled d-flex align-items-center">
                        <li className="me-3">
                            <button 
                                className="btn btn-outline-dark"
                                onClick={handleCartClick}
                            >
                                Cart
                            </button>
                        </li>
                        {isAuthenticated ? (
                            <>
                                <li className="me-3">
                                    <span className="text-dark">Xin ch√†o, {username}</span>
                                </li>
                                <li>
                                    <button 
                                        className="btn btn-danger"
                                        onClick={handleLogout}
                                    >
                                        Logout
                                    </button>
                                </li>
                            </>
                        ) : (
                            <li>
                                <button 
                                    className="btn btn-danger"
                                    onClick={handleLoginClick}
                                >
                                    Login
                                </button>
                            </li>
                        )}
                    </ul>
                </nav>
            </div>
            <div className="search-container">
                <input type="text" className="form-control" placeholder="Search for book, author..." />
                <button className="btn btn-danger">Search</button>
            </div>
        </header>
    );
};

export default Header;
