import React from 'react';
import { Link } from 'react-router-dom';
import './Sidebar.css';
import {logo} from '../services/authService';

const Sidebar = () => {
    const navigate = useNavigate();

    const handleLogout = () => {
        logout(navigate);
    };

    return (
        <div className="sidebar">
            <h2 className="sidebar-title">Thông tin tài khoản</h2>
            <ul className="sidebar-menu">
                <li>
                    <Link to="/cart">Giỏ hàng</Link>
                </li>
                <li>
                    <Link to="/invoices">Hóa đơn</Link>
                </li>
                <li>
                    <Link to="/account">Thông tin tài khoản</Link>
                </li>
                <li>
                    <button onClick={handleLogout} className="sidebar-logout-button">Đăng xuất</button>
                </li>
            </ul>
        </div>
    );
};

export default Sidebar;
