import React, { useEffect, useState } from 'react';
import { getAuthHeaders } from '../../services/userAuth';

const API_BASE_URL = 'http://localhost:5231';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}/api/cart`, {
          method: 'GET',
          headers: getAuthHeaders(),
        });

        if (!response.ok) {
          throw new Error(`Error fetching cart: ${response.status}`);
        }

        const data = await response.json();
        setCartItems(data);
        calculateTotal(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchCartItems();
  }, []);

  const calculateTotal = (items) => {
    const totalAmount = items.reduce((acc, item) => acc + item.price * item.quantity, 0);
    setTotal(totalAmount);
  };

  const handleQuantityChange = (id, quantity) => {
  };

  const handleCheckout = () => {
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="cart-container">
      <h2>Giỏ sách của bạn</h2>
      {cartItems.length === 0 ? (
        <p>Giỏ hàng của bạn đang trống.</p>
      ) : (
        <div>
          {cartItems.map(item => (
            <div key={item.cartId} className="cart-item">
              <img src={item.bookImage} alt={item.title} />
              <div>
                <h4>{item.title}</h4>
                <p>Giá: {item.price} VNĐ</p>
                <label>
                  Số lượng:
                  <input
                    type="number"
                    value={item.quantity}
                    min="1"
                    onChange={(e) => handleQuantityChange(item.cartId, e.target.value)}
                  />
                </label>
              </div>
            </div>
          ))}
          <div className="total">
            <h3>Tổng tiền: {total} VNĐ</h3>
            <button onClick={handleCheckout}>Mua hàng</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
