import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Header from '../../components/Header';
import '../../components/BookDetail.css';
import { getUserIdFromToken } from '../../services/userAuth';

const API_BASE_URL = 'http://localhost:5231';

const BookDetails = () => {
  const { id } = useParams();
  const [book, setBook] = useState(null);
  const [genreData, setGenre] = useState(null);
  const [publisherData, setPublisher] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBook = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}/api/book/${id}`);
        
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const bookData = await response.json();
        setBook(bookData);
        if (bookData.GenreId) {
          const genreResponse = await fetch(`${API_BASE_URL}/api/genre/${bookData.GenreId}`);
          if (genreResponse.ok) {
            const genreData = await genreResponse.json();
            setGenre(genreData);
          } else {
            console.error('Error fetching genre:', genreResponse.status);
          }
        }
        if (bookData.PublisherId) {
          const publisherResponse = await fetch(`${API_BASE_URL}/api/publisher/${bookData.PublisherId}`);
          if (publisherResponse.ok) {
            const publisherData = await publisherResponse.json();
            setPublisher(publisherData);
          } else {
            console.error('Error fetching publisher:', publisherResponse.status);
          }
        }

        setLoading(false);
      } catch (error) {
        console.error('Error fetching book:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchBook();
  }, [id]);

  const handleAddToCart = async () => {
    const userId = getUserIdFromToken();
    if (!userId) {
      alert('Vui lòng đăng nhập trước khi thêm vào giỏ hàng.');
      return;
    }

    const payload = {
      userId: userId,
      bookId: book.bookId,
      quantity: parseInt(quantity),
    };

    try {
      const response = await fetch(`${API_BASE_URL}/api/cart`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(payload),
      });

      if (response.ok) {
        alert('Đã thêm vào giỏ hàng thành công!');
      } else {
        const err = await response.json();
        alert(`Lỗi khi thêm vào giỏ hàng: ${err.message || response.statusText}`);
      }
    } catch (err) {
      console.error('Lỗi khi gọi API:', err);
      alert('Đã xảy ra lỗi, vui lòng thử lại sau.');
    }
  };


  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!book) return <div>No book found.</div>;

  return (
    <div>
      <Header />
      <div className="container mt-4">
        <div className="book-info">
          <div className="row">
            <div className="col-md-12">
              <h2>{book.title}</h2>
              <p><strong>Tác giả:</strong> {book.author}</p>
              <p><strong>Nhà xuất bản:</strong> {publisherData.publisherName}</p>
              <p><strong>Thể loại:</strong> {genreData.genreName}</p>
              <p><strong>ISBN:</strong> {book.isbn}</p>
              <p><strong>Năm xuất bản:</strong> {book.publishedYear}</p>
              <p><strong>Số lượng:</strong> {book.quantity}</p>
              <p><strong>Giá:</strong> {book.price} VNĐ</p>
              <div className="mb-3">
                <label htmlFor="quantity" className="form-label">Chọn số lượng:</label>
                <input
                  type="number"
                  id="quantity"
                  className="form-control"
                  value={quantity}
                  onChange={(e) => setQuantity(e.target.value)}
                  min="1"
                  max={book.quantity}
                />
              </div>
              <button className="btn btn-primary" onClick={handleAddToCart}>
                Thêm vào giỏ hàng
              </button>
            </div>
          </div>
        </div>

        <div className="reviews-section mt-5">
          <h3>Đánh giá</h3>
          {book.Reviews && book.Reviews.length > 0 ? (
            book.Reviews.map((review) => (
              <div key={review.reviewId} className="review-card mb-3">
                <p><strong>Rating:</strong> {review.rating}</p>
                <p>{review.comment}</p>
              </div>
            ))
          ) : (
            <p>Chưa có đánh giá nào.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default BookDetails;
