const API_BASE_URL = 'http://localhost:5231';

const SearchResults = () => {
    const [books, setBooks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const location = useLocation();

    useEffect(() => {
        const fetchBooks = async () => {
            const params = new URLSearchParams(location.search);
            const query = params.get('query');
            const genreId = params.get('genreId');
            let url = `${API_BASE_URL}/api/book`;

            if (query) {
                url += `/search/${query}`; // Search by title
            } else if (genreId) {
                url += `/genre/${genreId}`; // Filter by genre
            }

            try {
                const response = await fetch(url);
                if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
                const data = await response.json();
                setBooks(data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching books:', error);
                setError(error.message);
                setLoading(false);
            }
        };

        fetchBooks();
    }, [location.search]);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div>
            <Header />
            <div className="container">
                <h2 className="text-center">Kết quả tìm kiếm</h2>
                <div className="card-container">
                    {books.length > 0 ? (
                        books.map(book => (
                            <div key={book.bookId} className="card">
                                <div className="card-body">
                                    <h5 className="card-title">{book.title}</h5>
                                    <p className="card-author">{book.author}</p>
                                    <p className="card-price">Giá: {book.price} VNĐ</p>
                                    <Link to={`/book/${book.bookId}`} className="btn">Chi tiết</Link>
                                </div>
                            </div>
                        ))
                    ) : (
                        <div className="no-results">Không tìm thấy sách nào.</div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default SearchResults;
