// InvoiceDetail.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { getAuthHeaders } from '../../services/userAuth';

const InvoiceDetail = () => {
    const { id } = useParams();
    const [invoice, setInvoice] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchInvoiceDetail = async () => {
            setLoading(true);
            try {
                const response = await axios.get(`api/invoice/${id}`, {
                    headers: getAuthHeaders(),
                });
                setInvoice(response.data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchInvoiceDetail();
    }, [id]);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="invoice-detail-container">
            <h2>Invoice Detail</h2>
            <p><strong>Invoice ID:</strong> {invoice.invoiceId}</p>
            <p><strong>Invoice Date:</strong> {new Date(invoice.invoiceDate).toLocaleDateString()}</p>
            <p><strong>Total Amount:</strong> {invoice.totalAmount.toFixed(2)} VNƒê</p>
            <p><strong>Status:</strong> {invoice.paymentStatus === 1 ? 'Has checkout' : 'Not checkout'}</p>

            <h3>Rental Details</h3>
            <table className="rental-details-table">
                <thead>
                    <tr>
                        <th>Book Title</th>
                        <th>Quantity</th>
                    </tr>
                </thead>
                <tbody>
                    {invoice.rental?.rentalDetails.map((detail) => (
                        <tr key={detail.rentalDetailId}>
                            <td>{detail.bookTitle}</td>
                            <td>{detail.quantity}</td>
                        </tr>
                    )) || (
                        <tr>
                            <td colSpan="2">No rental details available</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default InvoiceDetail;
