import React, { useEffect, useState } from 'react';
import { getAuthHeaders, getUserIdFromToken } from '../../services/userAuth';

const API_BASE_URL = 'http://localhost:5231';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCartItems = async () => {
      const userId = getUserIdFromToken();
      if (!userId) {
        setError("Không xác định được người dùng.");
        setLoading(false);
        return;
      }

      try {
        const response = await fetch(`${API_BASE_URL}/api/cart/user/${userId}`, {
          headers: getAuthHeaders(),
        });

        if (!response.ok) {
          throw new Error(`Lỗi lấy giỏ hàng: ${response.status}`);
        }

        const data = await response.json();
        setCartItems(data);
        calculateTotal(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchCartItems();
  }, []);

  const calculateTotal = (items) => {
    const totalAmount = items.reduce((acc, item) => acc + item.book.price * item.quantity, 0);
    setTotal(totalAmount);
  };

  const handleCheckout = () => {
    alert('Mua hàng thành công (giả lập)');
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <>
    </>
    
  );
};

export default Cart;
