import React, { useEffect, useState } from 'react';
import { getUserIdFromToken, getUserRoleFromToken, getAuthHeaders } from '../../services/userAuth';
import axios from 'axios';
import '../../components/Account.css';

const API_BASE_URL = 'http://localhost:5231';

const Account = () => {
    const [userInfo, setUserInfo] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [formData, setFormData] = useState({
        fullName: '',
        username: '',
        email: '',
        address: '',
        phoneNumber: '',
    });

    useEffect(() => {
        const fetchUserInfo = async () => {
            const userId = parseInt(getUserIdFromToken(), 10);
            
            if (isNaN(userId)) {
                setError("Không xác định được người dùng.");
                setLoading(false);
                return;
            }

            const role = getUserRoleFromToken();
            try {
                let response;
                if (role === '0') {
                    response = await axios.get(`${API_BASE_URL}/api/Lesse/${userId}`, {
                        headers: getAuthHeaders(),
                    });
                } else if (role === '2') {
                    response = await axios.get(`${API_BASE_URL}/api/Staff/${userId}`, {
                        headers: getAuthHeaders(),
                    });
                    setFormData({
                        fullName: response.data.fullName,
                        username: response.data.username,
                        email: response.data.email,
                        address: response.data.address,
                        phoneNumber: response.data.phoneNumber,
                    });
                } else {
                    setError("Vai trò không hợp lệ.");
                    setLoading(false);
                    return;
                }

                setUserInfo(response.data);
            } catch (error) {
                console.error("Error fetching staff info:", error);
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchUserInfo();
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const role = getUserRoleFromToken();
        try {
            let response;
            if (role === '2') {
                response = await axios.put(`${API_BASE_URL}/api/Staff/${userInfo.userId}`, formData, {
                    headers: getAuthHeaders(),
                });
            } else if (role === '0') {
                response = await axios.put(`${API_BASE_URL}/api/Lesse/${userInfo.userId}`, formData, {
                    headers: getAuthHeaders(),
                });
            }

            setUserInfo(response.data);
            alert("Cập nhật thông tin thành công!");
        } catch (error) {
            console.error("Error updating info:", error);
            setError("Cập nhật không thành công.");
        }
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="account-container">
            <h2 className="account-title">Thông tin tài khoản</h2>
            {userInfo ? (
                <div className="account-info">
                    <form onSubmit={handleSubmit}>
                        <div className="account-input-group">
                            <label className="account-label">Tên đầy đủ:</label>
                            <input type="text" className="account-input" name="fullName" value={formData.fullName} onChange={handleChange} />
                        </div>
                        <div className="account-input-group">
                            <label className="account-label">Tên đăng nhập:</label>
                            <input type="text" className="account-input" name="username" value={formData.username} onChange={handleChange} />
                        </div>
                        <div className="account-input-group">
                            <label className="account-label">Email:</label>
                            <input type="email" className="account-input" name="email" value={formData.email} onChange={handleChange} />
                        </div>
                        <div className="account-input-group">
                            <label className="account-label">Địa chỉ:</label>
                            <input type="text" className="account-input" name="address" value={formData.address} onChange={handleChange} />
                        </div>
                        <div className="account-input-group">
                            <label className="account-label">Số điện thoại:</label>
                            <input type="tel" className="account-input" name="phoneNumber" value={formData.phoneNumber} onChange={handleChange} />
                        </div>
                        <button type="submit" className="account-button">Cập nhật</button>
                    </form>
                </div>
            ) : (
                <p>Không có thông tin tài khoản nào.</p>
            )}
        </div>
    );
};

export default Account;
