import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Header.css';

const API_BASE_URL = 'http://localhost:5231';

const Header = () => {
    const navigate = useNavigate();
    const isAuthenticated = localStorage.getItem('token') !== null;
    const username = localStorage.getItem('username');
    const [genres, setGenres] = useState([]);

    useEffect(() => {
        const fetchGenres = async () => {
            try {
                const response = await fetch(`${API_BASE_URL}/api/genre`);
                if (!response.ok) {
                    throw new Error('Failed to fetch genres');
                }
                const data = await response.json();
                setGenres(data);
            } catch (error) {
                console.error('Error fetching genres:', error);
            }
        };

        fetchGenres();
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('userId');
        localStorage.removeItem('username');
        
        navigate('/login');
        
        window.location.reload();
    };

    const handleGenreSelect = (genreId) => {
        console.log(`Selected Genre ID: ${genreId}`);
    };

    return (
        <header className="header">
            <div className="logo">
                <h1 className="text-danger logo-title">BOOK RENTAL WEBSITE</h1>
            </div>
            <nav className="nav">
                <button 
                    className="btn btn-outline-dark cart-button"
                    onClick={() => navigate('/cart')}
                >
                    Giỏ hàng
                </button>
                <select className="form-select" onChange={(e) => handleGenreSelect(e.target.value)}>
                    <option value="">Danh mục sách</option>
                    {genres.map(genre => (
                        <option key={genre.genreId} value={genre.genreId}>
                            {genre.genreName}
                        </option>
                    ))}
                </select>
                {isAuthenticated ? (
                    <>
                        <span className="user-greeting">Xin chào, {username}</span>
                        <button 
                            className="btn btn-danger"
                            onClick={handleLogout}
                        >
                            Đăng xuất
                        </button>
                    </>
                ) : (
                    <button className="btn btn-danger" onClick={() => navigate('/login')}>
                        Đăng nhập
                    </button>
                )}
            </nav>
            <div className="search-container">
                <input type="text" className="form-control" placeholder="Tìm sách, tác giả..." />
                <button className="btn btn-danger">Tìm kiếm</button>
            </div>
        </header>
    );
};

export default Header;
