import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Header from '../../components/Header';
import '../../components/BookDetail.css';

const API_BASE_URL = 'http://localhost:5231';

const BookDetails = () => {
  const { id } = useParams();
  const [book, setBook] = useState(null);
  const [genreName, setGenreName] = useState('');
  const [publisherName, setPublisherName] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBook = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}/api/book/${id}`, {
          headers: {
            'Accept': 'application/json',
          }
        });

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const bookData = await response.json();
        setBook(bookData);
        setLoading(false);

        // Gọi API để lấy tên Genre
        if (bookData.GenreId) {
          const genreResponse = await fetch(`${API_BASE_URL}/api/genre/${bookData.GenreId}`);
          const genreData = await genreResponse.json();
          setGenreName(genreData.genreName);
        }

        // Gọi API để lấy tên Publisher
        if (bookData.PublisherId) {
          const publisherResponse = await fetch(`${API_BASE_URL}/api/publisher/${bookData.PublisherId}`);
          const publisherData = await publisherResponse.json();
          setPublisherName(publisherData.publisherName);
        }

      } catch (error) {
        console.error('Error fetching book:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchBook();
  }, [id]);

  const handleAddToCart = () => {
    console.log(`Added ${quantity} of ${book.title} to cart.`);
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!book) return <div>No book found.</div>;

  return (
    <div>
      <Header />
      <div className="container mt-4">
        <div className="book-info">
          <div className="row">
            <div className="col-md-12">
              <h2>{book.title}</h2>
              <p><strong>Tác giả:</strong> {book.author}</p>
              <p><strong>Nhà xuất bản:</strong> {publisherName || 'N/A'}</p>
              <p><strong>Thể loại:</strong> {genreName || 'N/A'}</p>
              <p><strong>ISBN:</strong> {book.isbn}</p>
              <p><strong>Năm xuất bản:</strong> {book.publishedYear}</p>
              <p><strong>Số lượng:</strong> {book.quantity}</p>
              <p><strong>Giá:</strong> {book.price} VNĐ</p>
              <div className="mb-3">
                <label htmlFor="quantity" className="form-label">Chọn số lượng:</label>
                <input
                  type="number"
                  id="quantity"
                  className="form-control"
                  value={quantity}
                  onChange={(e) => setQuantity(e.target.value)}
                  min="1"
                  max={book.quantity}
                />
              </div>
              <button className="btn btn-primary" onClick={handleAddToCart}>
                Thêm vào giỏ hàng
              </button>
            </div>
          </div>
        </div>

        <div className="reviews-section mt-5">
          <h3>Đánh giá</h3>
          {book.Reviews && book.Reviews.length > 0 ? (
            book.Reviews.map((review) => (
              <div key={review.reviewId} className="review-card mb-3">
                <p><strong>Rating:</strong> {review.rating}</p>
                <p>{review.comment}</p>
              </div>
            ))
          ) : (
            <p>Chưa có đánh giá nào.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default BookDetails;
