import React, { useEffect, useState } from 'react';
import { getUserIdFromToken, getUserRoleFromToken, getAuthHeaders } from '../../services/userAuth';
import axios from 'axios';
import '../../';

const API_BASE_URL = 'http://localhost:5231';

const Account = () => {
    const [userInfo, setUserInfo] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    
    useEffect(() => {
        const fetchUserInfo = async () => {
            const userId = getUserIdFromToken();
            const role = getUserRoleFromToken();
            if (!userId) {
                setError("Không xác định được người dùng.");
                setLoading(false);
                return;
            }

            try {
                let response;
                if (role === '0') {
                    response = await axios.get(`${API_BASE_URL}/api/lesse/${userId}`, {
                        headers: getAuthHeaders(),
                    });
                } else if (role === '1') {
                } else if (role === '2') {
                    response = await axios.get(`${API_BASE_URL}/api/staff/${userId}`, {
                        headers: getAuthHeaders(),
                    });
                } else {
                    setError("Vai trò không hợp lệ.");
                    setLoading(false);
                    return;
                }

                setUserInfo(response.data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchUserInfo();
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="account-container">
            <h2>Thông tin tài khoản</h2>
            {userInfo ? (
                <div>
                    <p><strong>Họ tên:</strong> {userInfo.FullName || userInfo.username}</p>
                    <p><strong>Tên đăng nhập:</strong> {userInfo.Username}</p>
                    <p><strong>Email:</strong> {userInfo.Email}</p>
                    <p><strong>Địa chỉ:</strong> {userInfo.Address}</p>
                    <p><strong>Số điện thoại:</strong> {userInfo.PhoneNumber}</p>
                    <p><strong>Ngày tạo:</strong> {new Date(userInfo.CreatedAt).toLocaleDateString()}</p>
                </div>
            ) : (
                <p>Không có thông tin tài khoản nào.</p>
            )}
        </div>
    );
};

export default Account;
