import React, { useEffect, useState } from 'react';
import { getAuthHeaders, getUserIdFromToken } from '../../services/userAuth';

const API_BASE_URL = 'http://localhost:5231';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCartItems = async () => {
      const userId = getUserIdFromToken();
      if (!userId) {
        setError("Kh√¥ng x√°c ƒë·ªãnh ƒë∆∞·ª£c ng∆∞·ªùi d√πng.");
        setLoading(false);
        return;
      }

      try {
        const response = await fetch(`${API_BASE_URL}/api/cart/user/${userId}`, {
          headers: getAuthHeaders(),
        });

        if (!response.ok) {
          throw new Error(`L·ªói l·∫•y gi·ªè h√†ng: ${response.status}`);
        }

        const data = await response.json();
        setCartItems(data);
        calculateTotal(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchCartItems();
  }, []);

  const calculateTotal = (items) => {
    const totalAmount = items.reduce((acc, item) => acc + item.book.price * item.quantity, 0);
    setTotal(totalAmount);
  };

  const handleCheckout = () => {
    alert('Mua h√†ng th√†nh c√¥ng (gi·∫£ l·∫≠p)');
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div style={{ display: 'flex', padding: '1rem', maxWidth: '100%' }}>
      <div style={{ flex: 2 }}>
        <h2>GI·ªé S√ÅCH C·ª¶A B·∫†N</h2>
        {cartItems.length === 0 ? (
          <p>Gi·ªè h√†ng c·ªßa b·∫°n ƒëang tr·ªëng.</p>
        ) : (
          cartItems.map(item => (
            <div key={item.cartId} style={{ display: 'flex', justifyContent: 'space-between', padding: '1rem', borderBottom: '1px solid #ccc' }}>
              <div>
                <strong>{item.book.title}</strong>
                <div>T√°c gi·∫£: {item.book.author}</div>
              </div>
              <div>
                <div>S·ªë l∆∞·ª£ng: {item.quantity}</div>
                <div>ƒê∆°n gi√°: <span style={{ color: 'red' }}>{item.book.price.toLocaleString()}ƒë</span></div>
              </div>
            </div>
          ))
        )}
      </div>

      <div style={{ flex: 1, paddingLeft: '2rem', borderLeft: '1px solid #ccc' }}>
        <h3>T·ªïng ti·ªÅn: <span style={{ color: 'blue' }}>{total.toLocaleString()}ƒë</span></h3>
        <button style={{ padding: '0.5rem 1rem', backgroundColor: 'orange', color: 'white', border: 'none', marginTop: '1rem' }}
          onClick={handleCheckout}>
          üõí Mua h√†ng
        </button>
      </div>
    </div>
  );
};

export default Cart;
