import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../../components/Invoice.css';
import { getUserIdFromToken, getAuthHeaders } from '../../services/userAuth';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';

const Invoices = () => {
    const [invoices, setInvoices] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchInvoices = async () => {
            setLoading(true);
            const userId = getUserIdFromToken();
            if (!userId) {
                setError("Không xác định được người dùng.");
                setLoading(false);
                return;
            }

            try {
                const response = await axios.get(`api/invoice/user/${userId}`, {
                    headers: getAuthHeaders(),
                });

                if (response.status === 404) {
                    setError("Không có hóa đơn nào.");
                } else {
                    setInvoices(response.data);
                }
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchInvoices();
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <>
            <Header />
            <Sidebar />
            <div className="invoices-container">
                <div className="header">
                    <h2>Invoice History</h2>
                </div>
                <table className="invoices-table">
                    <thead>
                        <tr>
                            <th>No</th>
                            <th>Invoice ID</th>
                            <th>Ngày đặt</th>
                            <th>Số tiền</th>
                            <th>Trạng thái</th>
                            <th>Xử lý</th>
                        </tr>
                    </thead>
                    <tbody>
                        {invoices.length > 0 ? (
                            invoices.map((invoice, index) => (
                                <tr key={invoice.invoiceId}>
                                    <td>{index + 1}</td>
                                    <td>{invoice.invoiceId}</td>
                                    <td>{new Date(invoice.invoiceDate).toLocaleDateString()}</td>
                                    <td>{invoice.totalAmount.toFixed(2)} VNĐ</td>
                                    <td>{invoice.paymentStatus === 1 ? 'Đã thanh toán' : 'Chưa thanh toán'}</td>
                                    <td>
                                        <button>Chi tiết</button>
                                    </td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="6">Không có hóa đơn nào.</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </>
    );
};

export default Invoices;
