import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Header.css';

const API_BASE_URL = 'http://localhost:5231';

const Header = () => {
    const navigate = useNavigate();
    const isAuthenticated = localStorage.getItem('token') !== null;
    const username = localStorage.getItem('username');
    const [genres, setGenres] = useState([]);

    useEffect(() => {
        const fetchGenres = async () => {
            try {
                const response = await fetch(`${API_BASE_URL}/api/genre`);
                if (!response.ok) throw new Error('Failed to fetch genres');
                const data = await response.json();
                setGenres(data);
            } catch (error) {
                console.error('Error fetching genres:', error);
            }
        };

        fetchGenres();
    }, []);

    const handleLoginClick = () => {
        if (!isAuthenticated) navigate('/login');
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('userId');
        localStorage.removeItem('username');
        navigate('/login');
        window.location.reload();
    };

    const handleGenreSelect = (genreId) => {
        console.log(`Selected Genre ID: ${genreId}`);
    };

    const handleLogoClick = () => {
        navigate('/'); // Điều hướng về trang chính
    };

    return (
        <header className="custom-header">
            <div className="top-row">
                <div className="logo" onClick={handleLogoClick}>
                    <div className="line1">BOOK RENTAL</div>
                    <div className="line2">WEBSITE</div>
                </div>
                <nav className="nav-links">
                    <button onClick={() => navigate('/cart')}>Cart</button>
                    {isAuthenticated ? (
                        <>
                            <span className="username">Xin chào, {username}</span>
                            <button onClick={handleLogout}>Logout</button>
                        </>
                    ) : (
                        <button onClick={handleLoginClick}>Login</button>
                    )}
                </nav>
            </div>
            <div className="bottom-row">
                <select onChange={(e) => handleGenreSelect(e.target.value)}>
                    <option value="">All Genre</option>
                    {genres.map((genre) => (
                        <option key={genre.genreId} value={genre.genreId}>
                            {genre.genreName}
                        </option>
                    ))}
                </select>
                <div className="search-bar">
                    <input type="text" placeholder="Search for book, author..."  class/>
                    <button>Search</button>
                </div>
            </div>
        </header>
    );
};

export default Header;
