import React, { useEffect, useState } from 'react';
import Header from '../components/Header';
import { Link } from 'react-router-dom';

const HomePage = () => {
  const [newBooks, setNewBooks] = useState([]);
  const [popularBooks, setPopularBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const newBooksResponse = await fetch('/api/book?sort=newest&limit=5');
        const newBooksData = await newBooksResponse.json();
        setNewBooks(newBooksData);
        
        const popularBooksResponse = await fetch('/api/book?sort=popular&limit=5');
        const popularBooksData = await popularBooksResponse.json();
        setPopularBooks(popularBooksData);

        setLoading(false);
      } catch (error) {
        console.error('Error fetching books:', error);
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  if (loading) {
    return <div className="text-center py-5">Đang tải...</div>;
  }

  return (
    <div className="bg-gray-50 min-h-screen">
      <Header />
      
      <div className="container mx-auto px-4 py-8">
        <div className="flex flex-col lg:flex-row gap-8">
          {/* Cột trái - Sách mới */}
          <div className="lg:w-2/3">
            <div className="bg-white rounded-lg shadow-md p-6">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-2xl font-bold text-gray-800">Sách mới</h2>
                <Link to="/books" className="text-blue-600 hover:underline">Xem toàn bộ →</Link>
              </div>
              
              <div className="space-y-4">
                {newBooks.map(book => (
                  <div key={book.id} className="border-b pb-4 last:border-b-0">
                    <div className="flex justify-between">
                      <div>
                        <h3 className="font-semibold text-lg">{book.title}</h3>
                        <p className="text-gray-600">{book.author}</p>
                      </div>
                      <div className="text-right">
                        <p className="text-red-500 font-medium">
                          {book.price.toLocaleString('vi-VN')}đ
                        </p>
                        {book.originalPrice && (
                          <p className="text-sm text-gray-400 line-through">
                            {book.originalPrice.toLocaleString('vi-VN')}đ
                          </p>
                        )}
                      </div>
                    </div>
                    <div className="mt-2 text-sm text-gray-500 line-clamp-2">
                      {book.description}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
          
          {/* Cột phải - Sách bán chạy */}
          <div className="lg:w-1/3">
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-bold text-gray-800 mb-4">Sách bán chạy</h2>
              
              <div className="space-y-4">
                {popularBooks.map((book, index) => (
                  <div key={book.id} className="flex items-start gap-3">
                    <span className="bg-gray-200 text-gray-700 rounded-full w-6 h-6 flex items-center justify-center text-sm">
                      {index + 1}
                    </span>
                    <div className="flex-1">
                      <h3 className="font-medium">{book.title}</h3>
                      <p className="text-sm text-gray-600">{book.author}</p>
                      <p className="text-red-500 text-sm mt-1">
                        {book.price.toLocaleString('vi-VN')}đ
                      </p>
                    </div>
                    <span className="text-xs bg-blue-100 text-blue-800 px-2 py-1 rounded">
                      Đã bán: {book.soldCount}
                    </span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
